import React, {useRef, useState, useMemo} from "react";
import Counter from "./components/Counter"
import ClassCounter from "./components/classCounter";
import './styles/app.css'
import PostItem from "./components/PostItem";
import PostList from './components/PostList';
import MyButton from "./components/UI/button/MyButton";
import MyInput from "./components/UI/input/MyInput";
import PostForm from "./components/PostForm";
import MySelect from "./components/UI/select/MySelect";

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: "aa", body: 'dd'},
    {id: 2, title: "bb", body: 'cc'},
    {id: 3, title: "cc", body: 'bb'},
    {id: 4, title: "dd", body: 'aa'},
  ]);

  const [selectedSort, setSelectedSort] = useState("");
  const [searchQuery, setSerchQuery] = useState("");

  const sortedPosts = useMemo(() => {
    console.log('ОТРРАБОТАЛА ФНУКЦИЯ SORTED POSTS')
    if (selectedSort){
      return [...posts].sort((a,b) => a[selectedSort].localeCompare(b[selectedSort]));
    }
    else{
      return posts;
    }
  }, [selectedSort, posts])

  const searchAndSortedPosts = useMemo(() => {
    return sortedPosts.filter(post => post.title.includes(searchQuery))
  }, [searchQuery, sortedPosts])

  let createPost = (newPost) => {
    setPosts([...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  let sortPosts = (sort) => {
    setSelectedSort(sort);
  }

  return (
    <div className="App">
      <PostForm create={createPost}/>
      <hr style={{margin: "15px 0"}}/>
      <div>
        <MyInput
          value={searchQuery}
          onChange={e => setSerchQuery(e.target.value)}
          placeholder="поиск"/>
        <MySelect
          value={selectedSort}
          onChange={sortPosts}
          defaultValue="сортировка по"
          options={[
            {value: 'title', name: 'По названию '},
            {value: 'body', name: 'По описанию '}
          ]}
        />
      </div>
      {posts.length
      ? 
      <PostList remove={removePost} posts={searchAndSortedPosts} title={'Посты про JS'}/>
      : 
      <h1 style={{textAlign: "center"}}>Постов не найдено</h1>}
    </div>
  );
}

export default App;
