import React, { useEffect, useState } from "react";
import { usePosts } from '../hooks/usePosts';
import { useFetching } from '../hooks/useFetching';
import PostService from '../API/PostService';
import { getPageCount } from '../utils/pages';
import MyButton from '../components/UI/button/MyButton';
import MyModal from '../components/UI/MyModal/MyModal';
import Loader from '../components/UI/loader/Loader';
import PostForm from '../components/PostForm';
import PostFilter from '../components/PostFilter';
import Pagination from '../components/UI/pagination/Pagination';
import PostList from '../components/PostList';
import { useRef } from "react";

function Post() {
  const [posts, setPosts] = useState([]);
  const [filter, setFilter] = useState({ sort: '', query: '' })
  const [modal, setModal] = useState(false);
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(1);
  const lastElement = useRef();
  const observer = useRef();
  console.log(lastElement.current);

  const sortedAndSearchPosts = usePosts(posts, filter.sort, filter.query);
  const [fetchPosts, isPostsLoading, postError] = useFetching(async () => {
    let response = await PostService.getAll(limit, page);
    let countTotal = response.headers['x-total-count'];
    setPosts([...posts, ...response.data]);
    setTotalPages(getPageCount(countTotal, limit))
  });

  useEffect(() => {
    let callback = function (entries, observer) {
      console.log('div в зоне видимости');
    };
    observer.current = new IntersectionObserver(callback, {});
    observer.current.observe(lastElement.current);
  }, [])

  useEffect(() => {
    fetchPosts(limit, page);
  }, [page]);

  let createPost = (newPost) => {
    setPosts([...posts, newPost])
    setModal(false);
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  const changePage = (page) => {
    setPage(page);
  }

  return (
    <div className="App">
      <MyButton style={{ marginTop: '15px' }} onClick={() => setModal(true)}>
        Создать пост
      </MyButton>
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost} />
      </MyModal>
      <hr style={{ margin: "15px 0" }} />
      <PostFilter filter={filter} setFilter={setFilter} />
      {postError &&
        <h1>Произошла ошибка {postError}</h1>
      }
      <PostList remove={removePost} posts={sortedAndSearchPosts} title={'Посты про JS'} />
      <div ref={lastElement} style={{ height: 20, background: "red" }}></div>
      {isPostsLoading &&
        <div style={{ display: "flex", justifyContent: "center", marginTop: "50px" }}><Loader /></div>
      }
      <Pagination
        page={page} changePage={changePage} totalPages={totalPages}
      />

    </div>
  );
}

export default Post;
