import { useState } from "react"

export let useFetching = (callback) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    let fetching = async () => {
        try {
            setIsLoading(true);
            await callback;
        } catch (e) {
            setError(e.message);
        }
        finally{
            setIsLoading(false);
        }
    }

    return [fetching, isLoading, error];
}
