import React from "react";

function App() {
  let likes = 0;
  const state = useState(0);

  function increment(){
    likes += 1;
    console.log(likes);
  }
  function decrement(){
    likes -= 1;
    console.log(likes);
  }
  return (
    <div className="App">
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default App;
