import React from 'react'
import { Switch, Redirect, Route } from 'react-router-dom/cjs/react-router-dom.min';import About from './../Pages/About';
import Post from './../Pages/Post';
import Error from './../Pages/Error';
import PostPage from '../Pages/PostPage';
const AppRouter = () => {
    return (
        <Switch>
            <Route path="/about">
                <About />
            </Route>
            <Route exact path="/posts">
                <Post />
            </Route>
            <Route exact path="/posts/:id">
                <PostPage />
            </Route>
            <Route path="/error">
                <Error />
            </Route>
            <Redirect to='/error' />
        </Switch>
    )
}

export default AppRouter
